import {ProcessId} from "./TypeAliases";
import {Task} from "./Task";

/**
 * This class is a sort of template to generate commands that can run in a command line terminal.
 */
export class Process {
  private _id: ProcessId;
  private _commandTemplate: string;
  /**
   * Some processes may depend of other processes.
   * This dependencies will be propagated to {@link Task tasks} generated by these dependant processes.
   */
  private _deps: Array<ProcessId>;

  constructor(id: ProcessId, commandTemplate: string, deps: Array<ProcessId>) {
    this._id = id;
    this._commandTemplate = commandTemplate;
    this._deps = deps;
  }

  get id(): ProcessId {
    return this._id;
  }

  set id(value: ProcessId) {
    this._id = value;
  }

  get commandTemplate(): string {
    return this._commandTemplate;
  }

  set commandTemplate(value: string) {
    this._commandTemplate = value;
  }

  get deps(): Array<ProcessId> {
    return this._deps;
  }

  set deps(value: Array<ProcessId>) {
    this._deps = value;
  }
}